    public void connect(String node) {
        cluster = Cluster.builder()
                .addContactPoint(node)
                .build();
        Metadata metadata = cluster.getMetadata();
        System.out.printf("Connected to cluster: %s\n",
                metadata.getClusterName());
        for (Host host : metadata.getAllHosts()) {
            System.out.printf("Datatacenter: %s; Host: %s; Rack: %s\n",
                    host.getDatacenter(), host.getAddress(), host.getRack());
        }
        session = cluster.connect();
    }
    
    public void close() {
        session.close();
        cluster.close();
    }

    public void insert2currentxy(String shortName, double x, double y) {
        if (bound_insert2currentxy == null) {
            stat_insert2currentxy = session.prepare(
                    "insert into repoble.currentxyTRI(shortname, x, y)"
                    + " VALUES (?,?,?);");
            bound_insert2currentxy = new BoundStatement(stat_insert2currentxy);
        }
        session.execute(bound_insert2currentxy.bind(shortName, x, y));

        // client.insert2raw2process("TTT","TRAINING",1462606130L,"100","A234",false);
    }
    
    public void howMany2Process() {
        // select count(*) nb2proc from repoble.raw2process where period = 'TEST' and mode='T' and processed=false;
        ResultSet results = session.execute("SELECT count(*) as count  FROM repoble.raw2search "
                + "WHERE  processed=false");
        for (Row row : results) {
            System.out.println(row.getLong("count"));
        }
        System.out.println();
    }
    
    public long last2Process() {
        // select count(*) nb2proc from repoble.raw2process where period = 'TEST' and mode='T' and processed=false;
        ResultSet results = session.execute("SELECT max(ts10) as count  FROM repoble.raw2search "
                + "WHERE  processed=false");
        long res = 0;
        for (Row row : results) {
            res = row.getLong("count");
            System.out.println(res);
        }
        System.out.println();
        return res;
    }
    
    public int getRaspiAvg(long ts10, String shortname, String raspid) {
        // select raspid, avg(a) from repoble.rawinfo WHERE period = shortname='X4' and ts10=1463748990 and raspid='101';
        ResultSet results = session.execute("SELECT avg(a) as avg FROM repoble.rawsearch "
                + "WHERE shortname='" + shortname + "' and ts10=" + ts10 + " and raspid='" + raspid + "'");
        int result = 0;
        for (Row row : results) {
            int a = row.getInt("avg");
            if (a == 0) {
                return 0;
            } else {
                return Math.max(100 + row.getInt("avg"), 0);
            }
        }
        //System.out.println(ts10+","+shortname+","+raspid+" avg="+ts10+","+ts10+","+ts10+","+result);
        return result;
    }
    
    public float[] getPosXY(int[] raspival) {
        boolean verbose = true;
        float[] resxy = new float[2];
        double[] val=new double[raspival.length];
       
         for (int i = 0; i < raspival.length; i++) {
                val[i]=raspival[i]-100;
            } 
         if (verbose) {
            for (int i = 0; i < raspival.length; i++) {
                System.out.print(val[i] + ", ");
            }
            System.out.println();
        }
//        String prediction = Classify.advise7(crossValue(raspival));
//        String[] part = prediction.split("-");
        
        double[] resdouble=Trilateration.getPosXY(val, raspiPosition, "X999");
        
        resxy[0] = ((float) resdouble[0]);
        resxy[1] = ((float) resdouble[1]);
        
        if (verbose) {
            System.out.println("trilateration: " + resxy[0] + ", " + resxy[1]);
        }
        return resxy;
    }
    
    public static void getShortName(String filename) {
        InputStreamReader data = null;
        try {
            data = new InputStreamReader(new FileInputStream(filename), "UTF-8");
            BufferedReader in = new BufferedReader(data);
            String w = in.readLine();
            while (w != null) {
                shortName.add(w);
                System.out.println("searched:" + w);
                w = in.readLine();
            }
        } catch (FileNotFoundException ex) {
            Logger.getLogger(GetDataFromFile.class.getName()).log(Level.SEVERE, null, ex);
        } catch (UnsupportedEncodingException ex) {
            Logger.getLogger(GetDataFromFile.class.getName()).log(Level.SEVERE, null, ex);
        } catch (IOException ex) {
            Logger.getLogger(GetDataFromFile.class.getName()).log(Level.SEVERE, null, ex);
        }
    }
    
    public static void Locate(ComputeLocationTri_ts10_moy client) {
        
        long current = client.last2Process() - 10;  // on prend l'avant-dernier tic ...
        System.out.println("process at time :" + current);
        long step = 10;
        int nbperiod = 20;
        long[] tsSerie = new long[nbperiod];
        for (int i = 0; i < nbperiod; i++) {
            tsSerie[i] = current - i * step;
        }
        for (int i = 0; i < shortName.size(); i++) {
            LocateBLETri_ts10_moy ble = new LocateBLETri_ts10_moy(client, tsSerie, shortName.get(i));
            System.out.println("process:" + ble.getBleName());
            ble.getMeasures();  // get value from DB
            ble.estimatePosition(); // get x,y from ....
            ble.finishEstimation(); // avg of x,y
            System.out.println(ble.getBleName() + " XY:" + ble.getX() + "," + ble.getY());
            client.insert2currentxy(ble.getBleName(), ble.getX(), ble.getY());
        }
    }
    
